content.css:
body *,
html *,
body *::before,
html *::before,
body *::after,
html *::after {
  box-sizing: border-box;
}
#logbox {
  display: block;
  width: 100%;
  position: absolute;
  bottom: 0;
  left: 0;
  z-index: 999999;
  cursor: default;
  background-color: #f4f4f4;
  margin: 0;
  height: 1px;
  padding: 0px;
}
#logbox textarea {
  width: 100%;
  font-size: 20px;
  height: 100px;
  display: block;
  background-color: #dbe8fb;
  color: #616161;
  border: #aeaeae solid 1px;
  resize: none;
  padding: 15px;
}
#logbox #show_hidden {
  position: absolute;
  top: -25px;
  right: 30px;
  display: block;
  background-color: #f4f4f4;
  color: #000;
  width: 50px;
  text-align: center;
  font-size: 20px;
  border-radius: 7px 7px 0 0;
  font-weight: bold;
  cursor: pointer;
}
#logbox #show_hidden::before {
  content: '˄';
}
#logbox.open {
  padding: 15px;
  height: 60% !important;
}
#logbox.open #allcontent {
  display: block;
}
#logbox.open #show_hidden::before {
  content: '˅';
}
#logbox #allcontent {
  display: none;
  overflow: scroll;
  display: block;
  width: 100%;
  height: 100%;
  outline: #ddd solid 2px;
  padding: 7px;
}
#logbox .container-message {
  display: block;
  width: 100%;
  padding: 10px;
  background-color: #fff;
  border-radius: 8px;
  margin-bottom: 30px;
}
#logbox .container-message.error {
  color: red;
}
#logbox .container-message.information {
  color: green;
}
#logbox .container-message.warning {
  color: orange;
}
