::-webkit-scrollbar-thumb {
  background-color: rgba(2, 66, 30, 0.527);
  border-radius: 200px;
}
table {
  border-spacing: 0px;
  border-collapse: collapse;
  border: 0px;
  /*td:not(.is_row_account, .is_row_separator) {
        border: #add3f8 1px solid;
    }*/
}
table tbody td span {
  border: transparent 1px solid;
  border-top-width: 0px;
  border-right-width: 0px;
}
table tbody td:nth-child(1) span,
table tbody td:nth-child(4) span,
table tbody td:nth-child(5) span {
  border-right: #000 2px solid !important;
}
table .is_row_separator {
  border-color: transparent !important;
}
