.tbl_style tr.hidden {
  display: none;
}
.tbl_style tr td,
.tbl_style tr th {
  padding: 0;
}
.tbl_style tr td span,
.tbl_style tr th span {
  display: block;
  padding: 8px 10px;
}
.tbl_style tr td:first-child,
.tbl_style tr th:first-child {
  /*min-width: 340px;
                width: 390px;
                max-width: 390px;
*/
}
.tbl_style tr td:nth-child(n+2),
.tbl_style tr th:nth-child(n+2) {
  min-width: 120px;
}
.tbl_style tr td:nth-child(n+2),
.tbl_style tr th:nth-child(n+2),
.tbl_style tr td:nth-child(n+2) span,
.tbl_style tr th:nth-child(n+2) span {
  width: 190px;
}
.tbl_style tr td:last-child,
.tbl_style tr th:last-child,
.tbl_style tr td:last-child span,
.tbl_style tr th:last-child span {
  width: 120px;
  min-width: 100px;
}
.tbl_style tr td:last-child span,
.tbl_style tr th:last-child span {
  display: block;
}
.tbl_style tr td:nth-child(1),
.tbl_style tr th:nth-child(1) {
  position: sticky;
  z-index: 2;
  left: 0;
}
.tbl_style tr:not(.not_hover, .separator_row, .is_row_title_type_account, .headers):hover,
.tbl_style tr._show_ {
  background-color: #f7cc9f !important;
}
.tbl_style tr.headers {
  background-color: #007934;
  color: #fff;
  position: sticky;
  z-index: 999999;
  top: -1px;
}
.tbl_style .is_row_category {
  position: relative;
}
.tbl_style .is_row_category::after {
  position: absolute;
  top: 11px;
  left: 9px;
  display: block;
  content: '+';
  font-size: 16px;
  width: 17px;
  height: 17px;
  border: 1px solid #828282;
  text-align: center;
  line-height: 12px;
  color: #828282;
}
.tbl_style .is_row_category._show_::after {
  content: '−';
}
.tbl_style .is_row_category span {
  display: block;
  width: 100%;
  text-align: left;
  font-size: 16px;
  padding: 11px 10px 11px 33px;
  font-weight: bold;
  line-height: 1em;
}
.tbl_style tr:not(.is_row_category).is_row_group_acount span {
  padding: 5px 10px 5px 34px !important;
}
.tbl_style tr:not(.is_row_category).is_row_title span {
  padding: 5px 10px 5px 54px !important;
}
.tbl_style .is_row_title span {
  display: block;
  width: 100%;
  text-align: left;
  font-size: 16px;
  padding: 7px 15px;
  text-transform: capitalize;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}
.tbl_style .is_string_full {
  text-align: left;
}
.tbl_style .is_string_full span {
  width: 100% !important;
  max-width: 100% !important;
}
.tbl_style .is_string {
  text-align: left;
}
.tbl_style .is_row_title {
  font-style: italic;
  font-weight: 400;
}
.tbl_style .is_row_group_acount {
  font-weight: 600;
}
.tbl_style .is_row_title_type_account {
  background-color: #FF8300;
  text-align: left;
  color: #fff;
  font-size: 20px;
  padding: 7px 7px 7px 20px;
  font-weight: bold;
}
.tbl_style tfoot .is_row_title_type_account {
  font-size: 18px !important;
}
.tbl_style .is_row_title_account_green {
  background-color: #007934;
  text-align: left;
  color: #fff;
  font-size: 20px;
  padding: 7px 7px 7px 20px;
  font-weight: bold;
}
.tbl_style .is_row_account {
  background-color: #FF8300;
  text-align: left;
  color: #fff;
  font-size: 18px;
  font-weight: bold;
  position: relative;
}
.tbl_style .is_row_account span {
  display: block;
  width: 100%;
  text-align: right;
  padding: 7px 7px 7px 20px;
}
.tbl_style .is_row_account i {
  font-style: normal;
  display: block;
  position: absolute;
  left: 6px;
  top: 8px;
}
.tbl_style .is_numeric span {
  letter-spacing: 1px;
  text-align: right;
  position: relative;
  padding-left: 20px !important;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}
.tbl_style .is_numeric span i {
  font-style: normal;
  display: block;
  position: absolute;
  left: 6px;
  top: 8px;
}
.visual-tableDataAndGraph {
  padding: 0px !important;
}
#TblContainerER {
  cursor: default;
  width: 100%;
  height: 99%;
  overflow-x: hidden;
  overflow-y: scroll;
}
